import { Renderer2, EventEmitter, Output, Component, Input, OnInit, AfterViewInit, ViewChildren, QueryList, ElementRef } from '@angular/core';
import { VideosService } from 'src/app/services/videos.service';
import { CdkDragEnd } from '@angular/cdk/drag-drop';

@Component({
  selector: 'app-videos',
  templateUrl: './videos.component.html',
  styleUrls: ['./videos.component.scss']
})
export class VideosComponent implements OnInit, AfterViewInit {
  videos = this.videoService.getVideos();
  filteredVideos: any[] = [];
  isLoading = false;
  selectedKeywords: string[] = [];
  count = 0;

  @Input('keywords') keywords!: string[];
  @Output('triggerEvaluation') triggerEvaluation = new EventEmitter<string[]>();
  @ViewChildren('videoElement') videoRefs!: QueryList<ElementRef>;

  constructor(
    private videoService: VideosService,
    private renderer: Renderer2,
  ) {}

  ngOnInit() {
    this.filterVideos(this.keywords);
    this.renderer.listen('document', 'keydown', this.handleKeyboardEvent.bind(this));
  }

  ngAfterViewInit(): void {}

  filterVideos(keywords: string[]) {
    this.filteredVideos = this.videos.filter((video: any) => {
      return video.keywords.some((keyword: string) => keywords.includes(keyword));
    });
  }

  onSwipe(event: CdkDragEnd, keywords: string[]) {
    this.count++;
    const xDifference = event.distance.x;
    const element = event.source.element.nativeElement;

    if (xDifference > 0) {
      this.selectedKeywords = [...this.selectedKeywords, ...keywords];
    }

    this.renderer.addClass(element, 'fade-out');

    if (this.filteredVideos.length === this.count) {
      this.triggerEvaluation.emit(this.selectedKeywords);
    }
  }

  handleKeyboardEvent(event: KeyboardEvent) {
    const keywords = this.getCurrentVideoKeywords();

    switch(event.key) {
      case 'ArrowLeft':
        this.swipeLeft(keywords);
        break;
      case 'ArrowRight':
        this.swipeRight(keywords);
        break;
      default:
        break;
    }
  }

  getCurrentVideoElement(): HTMLElement {
    let currentVideo: HTMLElement | null = null;

    for (let i = this.videoRefs.length - 1; i >= 0; i--) {
      const videoElem: HTMLElement = this.videoRefs.toArray()[i].nativeElement;
      if (!videoElem.classList.contains('fade-out')) {
        currentVideo = videoElem;
        break; // exit the loop once the video is found
      }
    }

    return currentVideo!;
  }

  getCurrentVideoKeywords(): string[] {
    let keywords: string[] = [];

    for (let i = this.videoRefs.length - 1; i >= 0; i--) {
      const videoElem: HTMLElement = this.videoRefs.toArray()[i].nativeElement;
      if (!videoElem.classList.contains('fade-out')) {
        keywords = this.filteredVideos[i].keywords;
        break; // exit the loop once the video is found
      }
    }

    return keywords;
  }

  swipeLeft(keywords: string[]) {
    const currentVideoElement = this.getCurrentVideoElement();
    this.animateSwipeLeft(currentVideoElement, () => {
      this.completeSwipe(-100, currentVideoElement, keywords);
    });
  }

  swipeRight(keywords: string[]) {
      const currentVideoElement = this.getCurrentVideoElement();
      this.animateSwipeRight(currentVideoElement, () => {
        this.completeSwipe(100, currentVideoElement, keywords);
      });
  }

  animateSwipeLeft(element: HTMLElement, onComplete: () => void) {
    let position = 0;
    const animate = () => {
      position -= 10; // Change this value to control the speed
      this.renderer.setStyle(element, 'transform', `translateX(${position}px)`);
      if (position > -100) { // The -100 here is the desired final translation
        requestAnimationFrame(animate);
      } else {
        onComplete();
      }
    };
    requestAnimationFrame(animate);
  }

  animateSwipeRight(element: HTMLElement, onComplete: () => void) {
    let position = 0;
    const animate = () => {
      position += 10; // Change this value to control the speed
      this.renderer.setStyle(element, 'transform', `translateX(${position}px)`);
      if (position < 100) { // The 100 here is the desired final translation
        requestAnimationFrame(animate);
      } else {
        onComplete();
      }
    };
    requestAnimationFrame(animate);
  }

  completeSwipe(direction: number, element: HTMLElement, keywords: string[]) {
    const fakeEvent: CdkDragEnd = {
      source: {
        element: {
          nativeElement: element
        }
      },
      distance: {
        x: direction,
        y: 0
      }
    } as any;

    this.onSwipe(fakeEvent, keywords);
  }
}
