import { Renderer2, ElementRef, Component, Input, OnInit, AfterViewInit } from '@angular/core';
import { VideosService } from 'src/app/services/videos.service';
import { CdkDragEnd } from '@angular/cdk/drag-drop';

@Component({
  selector: 'app-videos',
  templateUrl: './videos.component.html',
  styleUrls: ['./videos.component.scss']
})
export class VideosComponent implements OnInit, AfterViewInit {
  videos = this.videoService.getVideos();
  filteredVideos: any[] = [];
  isLoading = false;

  @Input('keywords') keywords!: string[];

  constructor(
    private videoService: VideosService,
    private renderer: Renderer2,
  ) {}

  ngOnInit() {
      this.filterVideos(this.keywords);
  }

  ngAfterViewInit(): void {
    // const videoElement = document.querySelector('video');

    // // Add an event listener to the window's load event
    // window.addEventListener('load', () => {
    //   // Simulate a click on the video element
    //   videoElement!.play();
    // });
  }

  filterVideos(keywords: string[]) {
    this.filteredVideos = this.videos.filter((video: any) => {
      return video.keywords.some((keyword: string) => keywords.includes(keyword));
    });
    console.log(this.filteredVideos);
  }

  answer(answer: string) {

  }

  onSwipe(event: CdkDragEnd) {
    const xDifference = event.distance.x;
    const element = event.source.element.nativeElement;

    if (xDifference > 0) {
      console.log("Swiped Right");
    } else if (xDifference < 0) {
      console.log("Swiped Left");
    }
    this.renderer.addClass(element, 'fade-out');
  }

}
